mapper.select:
SELECT po.post_id , ca.category_name , (SELECT count(*) FROM file_info fi WHERE fi.post_id = po.post_id) AS file_count , po.title , po.post_content , po.writer , po.write_dt , po.update_dt , po.view_count FROM post_info po INNER JOIN category_info ca ON (po.category_id = ca.category_id)
po.post_id = #{postId}
AND po.write_dt >= #{fromDt} AND po.write_dt <= #{toDt}
AND po.category_id = #{categoryId}
AND po.title LIKE concat('%',#{searchMessage},'%') OR po.post_content LIKE concat('%',#{searchMessage},'%') OR po.writer LIKE concat('%',#{searchMessage},'%')
ORDER BY po.write_dt DESC
LIMIT ${start}, 10;

SELECT
    count(po.post_id) AS post_count
FROM post_info po


        po.write_dt >= #{fromDt}


        AND po.write_dt <= #{toDt}


        AND po.category_id = #{categoryId}


        AND po.title LIKE concat('%',#{searchMessage},'%')
        OR po.post_content LIKE concat('%',#{searchMessage},'%')
        OR po.writer LIKE concat('%',#{searchMessage},'%')

mapper.update:
UPDATE post_info SET
    writer = #{writer}
    , title = #{title}
    , post_content = #{postContent}
  WHERE
      post_id= #{postId}

UPDATE post_info SET
    view_count = #{viewCount}
WHERE
    post_id= #{postId}
